import React from "react";
import { Link, withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../../config/Config";

import Api from "../../config/Api";
import Loading from "../../components/Loading";
import Panel from "../../components/Panel";
import Heading from "../../components/Heading";
import Box from "../../components/Box";
import Edit from "../../components/Edit";

import "../../styles/admin.css";

import Icon from "../../assets/login-icon.svg";
import ProfileIcon from "../../assets/profile-icon.svg";

class AdminClassroom extends React.Component {

    state = {
        edit: false,
        type: "add-classroom",

        classroom: {},
        users: [],

        loading: true
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
    }

    async loadData() {
        this.setState({ loading: true });

        const token = getStorageItem("token");
        const id = this.props.match.params.id;

        const call = await Api.getAdminClassroom(id, token);

        if (call.classroom && call.users) {
            const classroom = call.classroom;
            const users = call.users;

            users.sort((a, b) => {
                return a.lastname.localeCompare(b.lastname);
            });

            this.setState({
                classroom: classroom,
                users: users,
                loading: false
            });
        }
    }

    componentDidMount() {
        this.loadData();
    }

    render() {
        return(
            <div className="screen" id="admin-classroom">
                <Heading title={this.state.classroom.name} />

                <Panel
                    title="Administrate your classrooms"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.edit ? (
                    <Edit
                        type={this.state.type}
                        close={() => this.setState({ edit: false })}
                    />
                ) : null}

                <div className="body-panel">
                    {this.state.users.map((user) => (
                        <Box
                            item={{ name: user.firstname + " " + user.lastname }}
                            onClick={() => {}}
                            icon={ProfileIcon}
                        />
                    ))}
                </div>
            </div>
        )
    }
}

export default withRouter(AdminClassroom);
