import React from "react";

import Api from "../config/Api";

import "../styles/edit.css";

export default class Edit extends React.Component {

    state = {
        firstname: "",
        lastname: "",
        password: "",
        role: "student",
        admin: true
    }

    constructor() {
        super();
    }

    render() {
        const type = this.props.type;

        if (type === "add-user") {
            return (
                <div className="edit-screen" id="add-user">
                    <div className="edit">
                        <div className="title">Add user</div>

                        <div className="form">
                            <input className="field" type="text" value={this.state.firstname} onChange={(event) => this.setState({ firstname: event.target.value })} placeholder="First name" />
                            <input className="field" type="text" value={this.state.lastname} onChange={(event) => this.setState({ lastname: event.target.value })} placeholder="Last name" />
                            <input className="field" type="password" value={this.state.password} onChange={(event) => this.setState({ password: event.target.value })} placeholder="Password" />

                            <div className="choice">
                                <div className="item">Student</div>
                                <div className="item">Teacher</div>
                            </div>

                            <div className="choice">
                                <div className="item">Admin</div>
                                <div className="item">Regular</div>
                            </div>

                            <div className="button">Add</div>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
