import React from "react";

import { setStorageItem, getStorageItem, removeStorageItem } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";

import "../styles/dashboard.css";

import ProfileImage from "../assets/dashboard-icon.svg";

export default class Dashboard extends React.Component {

    state = {
        user: {},

        loading: true
    }

    constructor() {
        super();

        this.getUser = this.getUser.bind(this);
    }

    async getUser() {
        this.setState({ loading: true });

        const token = getStorageItem("token");

        const user = await Api.getUser(token);

        if (user.user) {
            this.setState({
                user: user.user,
                loading: false
            });
        }
    }

    componentDidMount() {
        this.getUser();
    }

    render() {
        var rawDate = new Date().toString().split(" ");
        var date = rawDate[0] + " " +  rawDate[2] + ", " + rawDate[1] + " " + rawDate[3];

        const user = this.state.user;

        return(
            this.state.loading ? <Loading /> : (
                <div className="screen" id="dashboard">
                    <div className="title-large">Dashboard<div className="date">{date}</div></div>

                    <div className="dashboard-panel">
                        <div className="info-panel">
                            <div className="title-large">Welcome back, {user.firstname}</div>
                            <div className="text-small">
                                Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel.
                            </div>
                        </div>

                        <img className="image" src={ProfileImage} />
                    </div>

                    <div className="body-panel">
                        <div className="average panel">
                            <div className="title-small">Results</div>
                        </div>

                        <div className="homework panel">
                            <div className="title-small">Homework</div>
                        </div>
                    </div>
                </div>
            )
        )
    }
}
