import React from "react";

import Api from "../config/Api";

import "../styles/edit.css";

export default class Edit extends React.Component {

    state = {
        firstname: "",
        lastname: "",
        password: "",
        role: "student",
        admin: false
    }

    constructor() {
        super();
    }

    render() {
        const type = this.props.type;

        if (type === "add-user") {
            return (
                <div className="edit-screen" id="add-user">
                    <div className="edit">
                        <div className="title">Add user</div>

                        <input className="input" type="text" value={this.state.firstname} onChange={(event) => this.setState({ firstname: event.target.value })} placeholder="First name" />
                        <input className="input" type="text" value={this.state.lastname} onChange={(event) => this.setState({ lastname: event.target.value })} placeholder="Last name" />
                        <input className="input" type="password" value={this.state.password} onChange={(event) => this.setState({ password: event.target.value })} placeholder="Password" />

                        <br />

                        <div className="choice">
                            <div className="item" onClick={() => this.setState({ role: "student" })} style={this.state.role === "student" ? { backgroundColor: "#FF6A77", color: "white" } : null}>Student</div>
                            <div className="item" onClick={() => this.setState({ role: "teacher" })} style={this.state.role === "teacher" ? { backgroundColor: "#FF6A77", color: "white" } : null}>Teacher</div>
                        </div>

                        <div className="choice">
                            <div className="item" onClick={() => this.setState({ admin: true })} style={this.state.admin ? { backgroundColor: "#FF6A77", color: "white" } : null}>Admin</div>
                            <div className="item" onClick={() => this.setState({ admin: false })} style={!this.state.admin ? { backgroundColor: "#FF6A77", color: "white" } : null}>Regular</div>
                        </div>

                        <br />
                        <br />

                        <div className="button">Add</div>
                    </div>
                </div>
            )
        }
    }
}
