import React from "react";
import { Link, useLocation, useHistory, withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, logout } from "../config/Config";

import Api from "../config/Api";

import "../styles/menu.css";

class Menu extends React.Component {

    state = {
        isAdmin: false
    }

    constructor() {
        super();

        this.getUserPermissions = this.getUserPermissions.bind(this);
    }

    async getUserPermissions() {
        const token = getStorageItem("token");
        const call = await Api.getUser(token);

        console.log(call);

        if (call.user && call.user.admin === 1) {
            this.setState({ isAdmin: true });
        }
    }

    componentDidMount() {
        this.getUserPermissions();
    }

    render() {
        return(
            <div id="menu">
                <div className="border" />

                <div className="logo">EduLabs</div>

                <div className="navigation">
                    {this.state.isAdmin ? (
                        <Link className="item" to="/admin">
                            <div className="icon" />
                            <div className="title">Admin</div>
                            <div style={{ flex: 1 }} />
                            <div className="selector" style={this.props.location.pathname.includes("admin") ? { opacity: 1 } : { opacity: 0 }} />
                        </Link>
                    ) : null}

                    <Link className="item" to="/dashboard">
                        <div className="icon" />
                        <div className="title">Dashboard</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname === "/dashboard" ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <Link className="item" to="/classes">
                        <div className="icon" />
                        <div className="title">Classes</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname === "/classes" ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <Link className="item" to="/subjects">
                        <div className="icon" />
                        <div className="title">Subjects</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname.includes("subjects") ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <Link className="item" to="/grades">
                        <div className="icon" />
                        <div className="title">Grades</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname === "/grades" ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <Link className="item" to="/settings">
                        <ion-icon name="settings-sharp"></ion-icon>
                        <div className="title">Settings</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname === "/settings" ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <Link className="item" to="/profile">
                        <div className="icon" />
                        <div className="title">Profile</div>
                        <div style={{ flex: 1 }} />
                        <div className="selector" style={this.props.location.pathname === "/profile" ? { opacity: 1 } : { opacity: 0 }} />
                    </Link>

                    <div style={{ flex: 1 }} />

                    <div className="item" onClick={() => {
                        logout();
                        this.props.history.push("/")
                    }}>
                        <div className="icon" />
                        <div className="title">Logout</div>
                        <div style={{ flex: 1 }} />
                        {/*<div className="selector" style={location.pathname === "/profile" ? { opacity: 1 } : { opacity: 0 }} />*/}
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(Menu);
