import React from "react";
import { Link, withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../../config/Config";

import Api from "../../config/Api";
import Loading from "../../components/Loading";
import Panel from "../../components/Panel";
import Heading from "../../components/Heading";
import Box from "../../components/Box";
import Edit from "../../components/Edit";

import "../../styles/admin.css";

import Icon from "../../assets/login-icon.svg";
import ProfileIcon from "../../assets/profile-icon.svg";

class AdminUser extends React.Component {

    state = {
        user: {},

        firstname: "",
        lastname: "",
        email: "",
        role: "",
        admin: 0,

        selectedClassroom: "",
        selectedSubjects: [],

        classrooms: [],
        subjects: [],

        loading: true,

        width: 0
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
        this.loadClassrooms = this.loadClassrooms.bind(this);
        this.loadSubjects = this.loadSubjects.bind(this);
        this.loadUserSubjects = this.loadUserSubjects.bind(this);
        this.selectSubject = this.selectSubject.bind(this);
    }

    async loadData() {
        const token = getStorageItem("token");
        const id = this.props.match.params.id;

        const call = await Api.getAdminUser(id, token);

        if (call.user) {
            this.setState({
                user: call.user,

                firstname: call.user.firstname,
                lastname: call.user.lastname,
                email: call.user.email,
                role: call.user.role,
                admin: call.user.admin,

                selectedClassroom: call.user.classroom.id
            });
        }
    }

    async loadClassrooms() {
        const token = getStorageItem("token");

        const call = await Api.getAdminClassrooms(token);

        if (call.classrooms) {
            var classrooms = call.classrooms;
            classrooms.sort((a, b) => {
                return a.name.split(".")[1].localeCompare(b.name.split(".")[1]);
            });

            this.setState({
                classrooms: classrooms,
            });
        }
    }

    async loadSubjects() {
        const token = getStorageItem("token");

        const call = await Api.getAdminSubjects(token);

        if (call.subjects) {
            var subjects = call.subjects;
            subjects.sort((a, b) => {
                return a.name.localeCompare(b.name);
            });

            this.setState({
                subjects: subjects,
            });
        }
    }

    async loadUserSubjects() {
        const id = this.props.match.params.id;
        const token = getStorageItem("token");

        const call = await Api.getAdminSubjectsByUser(id, token);

        if (call.subjects) {
            console.log(call)
        }
    }

    selectSubject(subjectId) {
        var subjects = this.state.selectedSubjects;

        if (subjects.includes(subjectId)) {
            const index = subjects.indexOf(subjectId);
            subjects.splice(index, 1);
        } else {
            subjects.push(subjectId);
        }

        this.setState({ selectSubjects: subjects });
    }

    componentDidMount() {
        this.loadData();
        this.loadClassrooms();
        this.loadSubjects();
        this.loadUserSubjects();
    }

    render() {
        const user = this.state.user;

        return(
            <div className="screen" id="admin-user">
                <Heading title="User" />

                <div className="body-panel">
                    <div className="panel top-panel">
                        <img className="profile-image" src={ProfileIcon} />

                        <div className="info-panel">
                            <div className="name">{user.firstname + " " + user.lastname}</div>
                            <div className="role">{user.role === "student" ? "Student" : "Teacher"}</div>

                            <div style={{ flex: 1 }} />

                            <div className="button">Save changes</div>
                        </div>
                    </div>

                    <div className="bottom-panel">
                        <div className="panel left-panel">
                            <div className="title-small">Personal info</div>

                            <div style={{ height: 15 }} />

                            <input className="input" type="text" placeholder="First name" value={this.state.firstname} onChange={(event) => this.setState({ firstname: event.target.value })} />
                            <input className="input" type="text" placeholder="Last name" value={this.state.lastname} onChange={(event) => this.setState({ lastname: event.target.value })} />
                            <input className="input" type="text" placeholder="E-mail" value={this.state.email} onChange={(event) => this.setState({ email: event.target.value })} />

                            <div style={{ height: 20 }} />

                            <div className="title-small">Role</div>

                            <div style={{ height: 15 }} />

                            <div className="button-panel">
                                <div className={"button" + (this.state.role === "student" ? "" : "-disabled")} onClick={() => this.setState({ role: "student" })}>Student</div>
                                <div style={{ width: 10 }} />
                                <div className={"button" + (this.state.role === "teacher" ? "" : "-disabled")} onClick={() => this.setState({ role: "teacher" })}>Teacher</div>
                            </div>

                            <div style={{ height: 20 }} />

                            <div className="title-small">Privileges</div>

                            <div style={{ height: 20 }} />

                            <div className="button-panel">
                                <div className={"button" + (this.state.admin === 1 ? "" : "-disabled")} onClick={() => this.setState({ admin: 1 })}>Admin</div>
                                <div style={{ width: 10 }} />
                                <div className={"button" + (this.state.admin === 0 ? "" : "-disabled")} onClick={() => this.setState({ admin: 0 })}>Regular</div>
                            </div>
                        </div>

                        <div className="right-panel">
                            <div className="panel classroom-panel">
                                <div className="title-small">Classroom</div>

                                <div className="list">
                                    {this.state.classrooms.map((classroom) => (
                                        <div className="item" onClick={() => this.setState({ selectedClassroom: classroom.id })} style={classroom.id === this.state.selectedClassroom ? { backgroundColor: "#5E81F4" } : null}>
                                            <img className="image" src={Icon} />
                                            <div style={{ flex: 1 }} />
                                            <div className="title" style={classroom.id === this.state.selectedClassroom ? { color: "white" } : null}>{classroom.name}</div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            <div className="panel subject-panel">
                                <div className="title-small">Subjects</div>

                                <div className="list">
                                    {this.state.subjects.map((subject) => (
                                        <div className="item" onClick={() => this.selectSubject(subject.id)} style={this.state.selectedSubjects.includes(subject.id) ? { backgroundColor: "#5E81F4" } : null}>
                                            <img className="image" src={Icon} />
                                            <div style={{ flex: 1 }} />
                                            <div className="title" style={this.state.selectedSubjects.includes(subject.id) ? { color: "white" } : null}>{subject.name}</div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(AdminUser);
