import React from "react";
import { Link, withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";
import Edit from "../components/Edit";

import "../styles/admin.css";

import Icon from "../assets/login-icon.svg";
import Box from "../components/Box";

export default class Admin extends React.Component {

    state = {
        edit: false,
        type: "add-classroom"
    }

    constructor() {
        super();
    }

    render() {
        return(
            <div className="screen" id="admin">
                <Heading title="Admin" />

                <Panel
                    title="Administrate your school"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.edit ? (
                    <Edit
                        type={this.state.type}
                        close={() => this.setState({ edit: false })}
                    />
                ) : null}

                <div className="body-panel">
                    <Box
                        item={{ name: "Users" }}
                        icon={Icon}
                        onClick={() => this.props.history.push("/admin/users")}
                    />

                    <Box
                        item={{ name: "Classrooms" }}
                        icon={Icon}
                        onClick={() => this.props.history.push("/admin/classrooms")}
                    />

                    <Box
                        item={{ name: "Create user" }}
                        icon={Icon}
                        onClick={() => this.setState({ edit: true, type: "add-user" })}
                    />

                    <Box
                        item={{ name: "Create classroom" }}
                        icon={Icon}
                        onClick={() => this.setState({ edit: true, type: "add-classroom" })}
                    />

                    <Box
                        item={{ name: "Create subject" }}
                        icon={Icon}
                        onClick={() => this.setState({ edit: true, type: "add-subject" })}
                    />
                </div>
            </div>
        )
    }
}
