import React from "react";
import { withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";

import Icon from "../assets/dashboard-icon.svg";

class Classes extends React.Component {

    state = {
        loading: true
    }

    constructor() {
        super();
    }



    componentDidMount() {
        if (isLogged()) {

        } else {
            this.props.history.push("/");
        }
    }

    render() {
        return(
            <div className="screen" id="classes">
                <Heading title="Classes" />

                <Panel
                    title={"Manage your students"}
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.loading ? (
                    <div className="loading-container">
                        <Loading />
                    </div>
                ) : (
                    <div className="body-panel">
                        {this.state.subjects.map((subject) => <Subject subject={subject} />)}
                    </div>
                )}
            </div>
        )
    }
}

export default withRouter(Classes);
