import React from "react";
import { withRouter, Link } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";
import Box from "../components/Box";

import "../styles/subjects.css";

import Icon from "../assets/login-icon.svg";

class Subjects extends React.Component {

    state = {
        subjects: [],

        loading: true
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
    }

    async loadData() {
        this.setState({ loading: true });

        const token = getStorageItem("token");
        const subjects = await Api.getSubjects(token);

        console.log(subjects);

        if (subjects.subjects) {
            var array = [];

            for (let i = 0; i < subjects.subjects.length; i++) {
                const subject = await Api.getSubject(subjects.subjects[i].subject_id, token)

                array.push(subject.subject);
            }

            this.setState({
                subjects: array,
                loading: false
            });

            console.log(array)
        } else {
            this.setState({ loading: false });
            alert("ERROR");
        }
    }

    componentDidMount() {
        if (isLogged()) {
            this.loadData();
        } else {
            this.props.history.push("/");
        }
    }

    render() {
        const user = this.state.user;

        return(
            <div className="screen without-bottom-padding" id="subjects">
                <Heading title="Subjects" />

                <Panel
                    title="Dive into your subjects"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.loading ? (
                    <div className="loading-container">
                        <Loading />
                    </div>
                ) : (
                    <div className="body-panel">
                        {this.state.subjects.map((subject) => (
                            <Box
                                item={subject}
                                onClick={() => this.props.history.push({ pathname: "/subjects/" + subject.name.replace(/\s+/g, '-').toLowerCase(), state: { id: subject.id } })}
                            />
                        ))}
                    </div>
                )}
            </div>
        )
    }
}

function Subject(props) {
    const subject = props.subject;
    const url = subject.name.replace(/\s+/g, '-').toLowerCase();

    return(
        <Link className="subject" to={{ pathname: "/subjects/" + url, state: { id: subject.id } }}>
            <img className="image" src={Icon} />
            <div className="name">{subject.name}</div>
        </Link>
    )
}

export default withRouter(Subjects);
