import React from "react";
import { withRouter, Link } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";
import Box from "../components/Box";

import "../styles/subjects.css";

import Icon from "../assets/login-icon.svg";

class Subjects extends React.Component {

    state = {
        subjects: [],

        loading: true
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
    }

    async loadData() {
        this.setState({ loading: true });

        const token = getStorageItem("token");

        const call = await Api.getUserSubjects(token);

        if (call.subjects) {
            var fetchedSubjects = [];

            this.setState({
                loading: false,
                subjects: call.subjects
            });
        }
    }

    componentDidMount() {
        if (isLogged()) {
            this.loadData();
        } else {
            this.props.history.push("/");
        }
    }

    render() {
        const user = this.state.user;

        return(
            <div className="screen without-bottom-padding" id="subjects">
                <Heading title="Subjects" />

                <Panel
                    title="Dive into your subjects"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                <div className="body-panel">
                    {this.state.subjects.map((subject) => (
                        <Box
                            item={subject}
                            onClick={() => this.props.history.push({ pathname: "/subjects/" + subject.id, state: { id: subject.id } })}
                        />
                    ))}
                </div>
            </div>
        )
    }
}

export default withRouter(Subjects);
