import React from "react";
import { Link, withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";
import Edit from "../components/Edit";

import "../../styles/admin.css";

import Icon from "../assets/login-icon.svg";

export default class AdminClassrooms extends React.Component {

    state = {
        edit: false,
        type: "add-classroom",

        classrooms: [],

        loading: true
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
    }

    async loadData() {
        this.setState({ loading: true });

        const token = getStorageItem("token");

        const call = await Api.getAdminClassrooms(token);

        if (call.classrooms) {
            this.setState({
                classrooms: call.classrooms,
                loading: false
            });
        }
    }

    componentDidMount() {
        this.loadData();
    }

    render() {
        return(
            <div className="screen" id="admin">
                <Heading title="Classrooms" />

                <Panel
                    title="Administrate your classrooms"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.edit ? (
                    <Edit
                        type={this.state.type}
                        close={() => this.setState({ edit: false })}
                    />
                ) : null}

                {this.state.loading ? (
                    <div className="loading-container">
                        <Loading />
                    </div>
                ) : (
                    <div className="body-panel">
                        {this.state.classrooms.map((classroom) => (
                            <Box
                                item={classroom}
                                onClick={() => this.props.history.push({ pathname: "/admin/classrooms/" + classroom.name.replace(/\s+/g, '-').toLowerCase(), state: { id: classroom.id } })}
                            />
                        ))}
                    </div>
                )}
            </div>
        )
    }
}
