import React from "react";

import { API_URL } from "./Config";

export default class Api extends React.Component {

    constructor() {
        super();
    }

    // AUTHENTIFICATION API CALLS

    static async login(data) {
        var headers = new Headers();
        headers.append("Content-Type", "application/json");

        var raw = JSON.stringify({
            email : data.email,
            password : data.password
        })

        var requestOptions = {
            method: "POST",
            headers: headers,
            body: raw,
            redirect: "follow"
        };

        return fetch(API_URL + "/api/auth/login", requestOptions)
            .then(response => response.json())
            .then(result => {
                return result
            })
            .catch(error => {
                return error
            });
    }

    static async getUser(token) {
        var headers = new Headers();
        headers.append("Content-Type", "application/json");
        headers.append("auth-token", token);

        var requestOptions = {
            method: "GET",
            headers: headers,
            redirect: "follow"
        };

        return fetch(API_URL + "/api/auth/user", requestOptions)
            .then(response => response.json())
            .then(result => {
                return result
            })
            .catch(error => {
                return error
            });
    }

    // PERSONAL API CALLS

    static async getUserSubjects(token) {
        var headers = new Headers();
        headers.append("Content-Type", "application/json");
        headers.append("auth-token", token);

        var requestOptions = {
            method: "GET",
            headers: headers,
            redirect: "follow"
        };

        return fetch(API_URL + "/api/subjects", requestOptions)
            .then(response => response.json())
            .then(result => {
                return result
            })
            .catch(error => {
                return error
            });
