import React from "react";
import { withRouter } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";
import Box from "../components/Box";

import "../styles/classrooms.css";

import Icon from "../assets/login-icon.svg";

class Classrooms extends React.Component {

    state = {
        classrooms: [],

        loading: true
    }

    constructor() {
        super();

        this.loadData = this.loadData.bind(this);
    }

    async loadData() {
        const token = getStorageItem("token");
        const subjectId = this.props.match.params.id;

        const call = await Api.getTeacherClassroomsBySubject(subjectId, token);


    }

    componentDidMount() {
        this.loadData();
    }

    render() {
        return(
            <div className="screen" id="classrooms">
                <Heading title="Classrooms" />

                <Panel
                    title={"Manage your classrooms"}
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                <div className="body-panel">
                    {this.state.classrooms.map((classroom) => (
                        <Box
                            item={classroom}
                            onClick={() => this.props.history.push("/classrooms/" + classroom.id)}
                        />
                    ))}
                </div>
            </div>
        )
    }
}

export default withRouter(Classrooms);
