import React from "react";
import { withRouter, Link } from "react-router-dom";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";
import Heading from "../components/Heading";

import "../styles/subject.css";

import Icon from "../assets/login-icon.svg";

class Subject extends React.Component {

    state = {
        subject: {},

        category: 2,

        loading: true
    }

    constructor() {
        super();

        this.loadSubject = this.loadSubject.bind(this);
        this.loadGrades = this.loadGrades.bind(this);
    }

    async loadSubject() {
        const { id } = this.props.location.state;
        const token = getStorageItem("token");

        const subject = await Api.getSubject(id, token);

        if (subject.subject) {
            this.setState({ subject: subject.subject, loading: false });
        }
    }

    async loadGrades() {
        const { id } = this.props.location.state;
        const token = getStorageItem("token");

        const grades = await Api.getGrades(id, token);

        console.log(grades);
    }

    componentDidMount() {
        if (isLogged()) {
            this.loadSubject();

            if (this.state.category === 2) {
                this.loadGrades();
            }
        } else {
            this.props.history.push("/");
        }
    }

    render() {
        const subject = this.state.subject;

        return(
            <div className="screen" id="subject">
                <Heading title="Subjects" />

                <Panel
                    title={subject.name}
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={Icon}
                />

                {this.state.loading ? (
                    <div className="loading-container">
                        <Loading />
                    </div>
                ) : (
                    <div className="body-panel">
                        <div className="menu">
                            <div className="item" onClick={() => this.setState({ category: 1 })}>Overview</div>
                            <div className="item" onClick={() => this.setState({ category: 2 })}>Grades</div>
                            <div className="item" onClick={() => this.setState({ category: 3 })}>Students</div>
                            <div className="item" onClick={() => this.setState({ category: 4 })}>Homework</div>
                        </div>

                        <div className="content">
                            {this.state.category === 1 ? <Overview /> : null}
                            {this.state.category === 2 ? <Grades /> : null}
                            {this.state.category === 3 ? <Students /> : null}
                            {this.state.category === 4 ? <Homework /> : null}
                        </div>
                    </div>
                )}
            </div>
        )
    }
}

function Overview(props) {
    return(
        <div>

        </div>
    )
}

function Grades(props) {
    return(
        <div className="grades">

        </div>
    )
}

function Students(props) {
    return(
        <div>

        </div>
    )
}

function Homework(props) {
    return(
        <div>

        </div>
    )
}

export default withRouter(Subject);
