import React from "react";

import { setStorageItem, getStorageItem, removeStorageItem } from "../config/Config";

import Api from "../config/Api";
import Loading from "../components/Loading";
import Panel from "../components/Panel";

import ProfileImage from "../assets/dashboard-icon.svg";

export default class Subjects extends React.Component {

    state = {
        user: {}
    }

    constructor() {
        super();

        this.getUser = this.getUser.bind(this);
    }

    async getUser() {
        const token = getStorageItem("token");

        const user = await Api.getUser(token);

        if (user.user) {
            this.setState({ user: user.user });
        }
    }

    componentDidMount() {
        this.getUser();
    }

    render() {
        var rawDate = new Date().toString().split(" ");
        var date = rawDate[0] + " " +  rawDate[2] + ", " + rawDate[1] + " " + rawDate[3];

        const user = this.state.user;

        return(
            <div className="screen" id="subjects">
                <div className="title-large">Subjects<div className="date">{date}</div></div>

                <Panel
                    title="Dive into your subjects"
                    text="Welcome back to EduLabs. You have new assignments, homeworks and grades. Look them up in your notification panel."
                    image={ProfileImage}
                />
            </div>
        )
    }
}
