import React from "react";

import { setStorageItem, getStorageItem, removeStorageItem, isLogged } from "../config/Config";

import Api from "../config/Api";
import Loading from "./Loading";

import "../styles/edit.css";

export default class Edit extends React.Component {

    state = {
        loading: false,

        firstname: "",
        lastname: "",
        password: "",
        role: "student",
        admin: 0,
        classroom: "",
        classrooms: [],

        classroomName: ""
    }

    constructor() {
        super();

        this.createUser = this.createUser.bind(this);
        this.getClassrooms = this.getClassrooms.bind(this);
    }

    async createUser() {
        this.setState({ loading: true });

        const token = getStorageItem("token");

        const call = await Api.createUser({
            firstname: this.state.firstname,
            lastname: this.state.lastname,
            email: this.state.firstname.toLowerCase() + "." + this.state.lastname.toLowerCase() + "@edulabs.sk",
            password: this.state.password,
            role: this.state.role,
            admin: this.state.admin
        }, token);

        if (call.message === "User registered successfully") {
            this.setState({ loading: false });

            this.props.close();
        }
    }

    async getClassrooms() {
        const token = getStorageItem("token");

        const call = await Api.getClassrooms(token);

        console.log(call);

        if (call.classrooms) {
            this.setState({ classrooms: call.classrooms });
        }
    }

    async createClassroom() {
        this.setState({ loading: true });

        const token = getStorageItem("token");

        const call = await Api.createClassroom({
            name: this.state.classroomName
        }, token);

        if (call.message === "Classroom created successfully") {
            this.setState({ loading: false });

            this.props.close();
        }
    }

    componentDidMount() {
        if (this.props.type === "add-user") {
            this.getClassrooms();
        }
    }

    render() {
        const type = this.props.type;

        if (type === "add-user") {
            if (this.state.loading) {
                return <div className="edit-screen"><div className="edit"><Loading /></div></div>;
            }

            return (
                <div className="edit-screen" id="add-user">
                    <div className="edit">
                        <div className="title">Add user<ion-icon name="close" onClick={() => this.props.close()}></ion-icon></div>

                        <input className="input" type="text" value={this.state.firstname} onChange={(event) => this.setState({ firstname: event.target.value })} placeholder="First name" />
                        <input className="input" type="text" value={this.state.lastname} onChange={(event) => this.setState({ lastname: event.target.value })} placeholder="Last name" />

                        <br />

                        <input className="input" type="password" value={this.state.password} onChange={(event) => this.setState({ password: event.target.value })} placeholder="Password" />

                        <br />
                        <br />

                        <div className="choice">
                            <div className="item" onClick={() => this.setState({ role: "student" })} style={this.state.role === "student" ? { backgroundColor: "#5E81F4", color: "white" } : null}>Student</div>
                            <div className="item" onClick={() => this.setState({ role: "teacher" })} style={this.state.role === "teacher" ? { backgroundColor: "#5E81F4", color: "white" } : null}>Teacher</div>
                        </div>

                        <br />

                        <div className="choice">
                            <div className="item" onClick={() => this.setState({ admin: 1 })} style={this.state.admin === 1 ? { backgroundColor: "#5E81F4", color: "white" } : null}>Admin</div>
                            <div className="item" onClick={() => this.setState({ admin: 0 })} style={this.state.admin === 0 ? { backgroundColor: "#5E81F4", color: "white" } : null}>Regular</div>
                        </div>

                        <br />

                        {this.state.role === "student" ? (
                            <div className="list">
                                <div className="slider">
                                    {this.state.classrooms.map((classroom) => <div className="classroom" onClick={() => this.setState({ classroom: classroom.id })} style={classroom.id === this.state.classroom ? { backgroundColor: "#5E81F4", color: "white" } : null}>{classroom.name}</div>)}
                                </div>
                            </div>
                        ) : null}

                        <br />
                        <br />

                        <div className="button" onClick={() => this.createUser()}>Add</div>
                    </div>
                </div>
            )
        } else if (type === "add-classroom") {
            if (this.state.loading) {
                return <div className="edit-screen"><div className="edit"><Loading /></div></div>;
            }

            return (
                <div className="edit-screen" id="add-classroom">
                    <div className="edit">
                        <div className="title">Add classroom<ion-icon name="close" onClick={() => this.props.close()}></ion-icon></div>

                        <input className="input" type="text" value={this.state.classroomName} onChange={(event) => this.setState({ classroomName: event.target.value })} placeholder="Classroom name" />

                        <br />
                        <br />

                        <div className="button" onClick={() => this.createClassroom()}>Add</div>
                    </div>
                </div>
            )
        }
    }
}
